template <typename DT> class Point { public: DT x, y, z; Point() {}; Point(DT x, DT y, DT z) : x(x), y(y), z(z) {} Point operator&(const Point& rhs) const { /* cross product */ return Point(y * rhs.z - z * rhs.y, z * rhs.x - x * rhs.z, x * rhs.y - y * rhs.x); } DT operator^(const Point& rhs) const { /* dot product */ return x * rhs.x + y * rhs.y + z * rhs.z; } friend DT DisSq(const Point& a, const Point& b) { return (a.x - b.x) * (a.x - b.x) + (a.y - b.y) * (a.y - b.y) + (a.z - b.z) * (a.z - b.z); } };